mapper.select:

 select * from (select t.*,rownum rn from (select * from t_person
	where 1=1 

		and name like '%'||#{name}||'%'


)t) where rn>=#{start} and rn<=#{end}


 select * from (select t.*,rownum rn from (select * from t_person
	where 1=1 

		and id like '%'||#{userid}||'%'


)t) where rn>=#{start} and rn<=#{end}


 select * from (select t.*,rownum rn from (select * from t_person
	where 1=1 

		and mobile like '%'||#{mobile}||'%'


)t) where rn>=#{start} and rn<=#{end}
